import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import { Typography, Grid, GridList, Paper } from "@material-ui/core";

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

export default function Articles() {
  const classes = useStyles();
  const [data, setData] = useState([]);

  const handleData = () => {
    axios
      .get("http://localhost:4000/articles/recherche", {})
      .then((resultat) => {
        setData(resultat.data);
      });
  };

  useEffect(() => {
    handleData();
  }, []);

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ID</TableCell>
            <TableCell>Nom</TableCell>
            <TableCell>Prenom</TableCell>
            <TableCell>E-mail</TableCell>
            <TableCell>Date de cr√©ation</TableCell>
            <TableCell align="center">Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow>
              <TableCell>{row._id}</TableCell>
              <TableCell>{row.nom}</TableCell>
              <TableCell>{row.prenom}</TableCell>
              <TableCell>{row.email}</TableCell>
              <TableCell>{row.cree}</TableCell>
              <TableCell align="center">
                <Button
                  color="primary"
                  onClick={() => {
                    setOpen(true);
                    setId(row._id);
                    setNom(row.nom);
                    setPrenom(row.prenom);
                    setEmail(row.email);
                  }}
                >
                  Modifier
                </Button>
                <Button
                  color="secondary"
                  onClick={() => {
                    axios
                      .post(
                        "http://localhost:4000/clients/supprimerclient",
                        { _id: row._id },
                        {
                          headers: {
                            Authorization:
                              "bearer " + localStorage.getItem("jwt-cookie"),
                          },
                        }
                      )
                      .then(() => {
                        setRows([]);
                        refreshRows();
                      });
                  }}
                >
                  Supprimer
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
