import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Commandes() {
  const [etat, setEtat] = useState("Attente");
  const [data, setData] = useState([]);
  useEffect(() => {
    setData([]);
    axios
      .post(
        "http://localhost:4000/commandes/recherche",
        { etat },
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
          },
        }
      )
      .then((resultat) => {
        resultat.data.forEach((r) => {
          setData((d) => [
            ...d,
            {
              adresse: r.adresse,
              description: description,
              client: r.client,
              liste: r.liste,
              prix: r.prix,
            },
          ]);
        });
      });
  }, [etat]);
  return <div>{console.log(JSON.stringify(data))}</div>;
}
