import React, { useEffect, useState } from "react";
import { Container, Button } from "@material-ui/core";
import axios from "axios";

export default function Panier() {
  const [data, setData] = useState([]);
  useEffect(() => {
    axios
      .post(
        "http://localhost:4000/clients/getpanier",
        {},
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie")
          }
        }
      )
      .then(resultat =>
        resultat.data
          .map(d => {
            const currentData = [];
            axios
              .get("http://localhost:4000/articles/recherche/" + d._id)
              .then(resultat => currentData.push(resultat.data.nom));
          })
          .then(() => console.log(currentData))
      );
  }, []);
  const handleSubmit = event => {
    event.preventDefault();
    if (!localStorage.getItem("jwt-cookie")) {
      window.location = "/Connexion";
    }
  };

  return (
    <Container component="main">
      <form onSubmit={handleSubmit} noValidate>
        <Button variant="contained" color="primary" type="submit">
          Commander
        </Button>
      </form>
    </Container>
  );
}
