import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Typography,
  Button,
  makeStyles,
  TextField,
  Grid
} from "@material-ui/core";
import Autocomplete from "@material-ui/lab/Autocomplete";

const useStyles = makeStyles(theme => ({
  container: {
    maxWidth: 350
  },
  paper: {
    margin: theme.spacing(2),
    display: "flex",
    flexDirection: "column"
  },
  spacing: {
    margin: theme.spacing(1)
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1)
  },
  submit: {
    margin: theme.spacing(3, 0, 2)
  }
}));

export default function Article() {
  const classes = useStyles();
  const [data, setData] = useState("");
  const [quantite, setQuantite] = useState([]);
  useEffect(() => {
    axios
      .get(
        "http://localhost:4000/articles/recherche/" +
          window.location.pathname.substring(9, window.location.pathname.length)
      )
      .then(resultat => {
        setData(resultat.data);
        for (let i = 1; i < resultat.data.quantite; i++) {
          setQuantite([
            ...quantite,
            {
              title: "1"
            }
          ]);
        }
        console.log(quantite);
      });
  }, []);
  return (
    <Grid container className={classes.container}>
      <div className={classes.paper}>
        <form className={classes.form} noValidate>
          <Typography variant="h5" className={classes.spacing}>
            {data.nom}
          </Typography>
          <Typography
            variant="h4"
            color="secondary"
            className={classes.spacing}
          >
            {data.prix} DT
          </Typography>
          <Autocomplete
            id="combo-box-demo"
            options={[{ title: "1" }]}
            getOptionLabel={option => option.title}
            renderInput={params => (
              <TextField {...params} label="quantitÃ©" variant="outlined" />
            )}
          />
          <Button
            variant="contained"
            color="primary"
            className={classes.submit}
            fullWidth
          >
            Ajouter au panier
          </Button>
        </form>
      </div>
    </Grid>
  );
}
