import React, { useEffect, useState } from "react";
import {
  Container,
  Button,
  Typography,
  Paper,
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: theme.spacing(2),
    margin: "auto",
  },
  total:{
    
  }
}));

export default function Panier() {
  const classes = useStyles();
  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const supprimerItem = (_id) => {
    if (localStorage.getItem("jwt-cookie")) {
      axios
        .post(
          "http://localhost:4000/clients/supprimerpanier",
          { _id },
          {
            headers: {
              Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
            },
          }
        )
        .then(() => setItems(items.filter((item) => item._id !== _id)));
    }
    if (localStorage.getItem("cart-cookie")) {
      let data = JSON.parse(localStorage.getItem("cart-cookie"));
      data = data.filter((d) => _id !== d._id);
      if (data.length === 0) localStorage.removeItem("cart-cookie");
      else localStorage.setItem("cart-cookie", JSON.stringify(data));
      setItems(items.filter((item) => item._id !== _id));
    }
  };
  const panierVide = () => {
    return (
      <Paper className={classes.paper}>
        <Typography>Panier Vide</Typography>
      </Paper>
    );
  };
  const affichage = () => {
    return (
      <TableContainer component={Paper}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>
                <Typography color="primary" component="h1">
                  Panier
                </Typography>
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {items.map((item) => (
              <TableRow>
                <TableCell>{item.nom}</TableCell>
                <TableCell align="left">{item.prix} DT</TableCell>
                <TableCell align="center">{item.quantite}</TableCell>
                <TableCell align="right" padding="checkbox">
                  <Button
                    color="secondary"
                    onClick={() => {
                      supprimerItem(item._id);
                    }}
                  >
                    Supprimer
                  </Button>
                </TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={3} align="left" className={classes.total}>
                <Typography>Total:</Typography>
                <Typography color="secondary"> {total} DT</Typography>
              </TableCell>
              <TableCell align="right" padding="checkbox">
                <Button color="primary" type="submit">
                  Commander
                </Button>
              </TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
    );
  };
  useEffect(() => {
    setTotal(0);
    if (localStorage.getItem("jwt-cookie")) {
      axios
        .post(
          "http://localhost:4000/clients/getpanier",
          {},
          {
            headers: {
              Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
            },
          }
        )
        .then((resultat) => {
          resultat.data.forEach((d) => {
            axios
              .get("http://localhost:4000/articles/recherche/" + d._id)
              .then((resultat) => {
                setItems((items) => [
                  ...items,
                  {
                    _id: d._id,
                    nom: resultat.data.nom,
                    quantite: d.quantite,
                    prix: resultat.data.prix,
                  },
                ]);
                setTotal((t) => t + resultat.data.prix * d.quantite);
              });
          });
        });
    } else if (localStorage.getItem("cart-cookie")) {
      const resultat = JSON.parse(localStorage.getItem("cart-cookie"));
      resultat.forEach((d) => {
        axios
          .get("http://localhost:4000/articles/recherche/" + d._id)
          .then((resultat) => {
            setItems((items) => [
              ...items,
              {
                _id: d._id,
                nom: resultat.data.nom,
                quantite: d.quantite,
                prix: resultat.data.prix,
              },
            ]);
            setTotal((t) => t + resultat.data.prix * d.quantite);
          });
      });
    }
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!localStorage.getItem("jwt-cookie")) {
      window.location = "/Connexion";
    }
  };

  return (
    <Container component="main">
      <form onSubmit={handleSubmit} noValidate>
        {items.length === 0 ? panierVide() : affichage()}
      </form>
    </Container>
  );
}
