import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import { Autocomplete } from "@material-ui/lab";
import {
  Typography,
  TextField,
  Grid,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormLabel,
  FormControl,
  GridList,
  Paper,
  Link,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(3, 3, 3, 1),
  },
  Box: {
    margin: theme.spacing(1),
    width: 200,
  },
  Text: {
    margin: theme.spacing(0, 1),
    textAlign: "center",
  },
  image: {
    maxWidth: 195,
    height: 125,
    display: "block",
    margin: "auto",
  },
  padding: {
    padding: theme.spacing(1, 0),
  },
}));

export default function Acceuil() {
  const classes = useStyles();
  const [categories, setCategories] = useState([]);
  const [marques, setMarques] = useState([]);
  const [categorie, setCategorie] = useState("");
  const [marque, setMarque] = useState("");
  const [data, setData] = useState([]);

  const handleData = (value) => {
    axios
      .post("http://localhost:4000/articles/recherche", {
        prix: value,
        categorie: categorie,
        marque: marque,
      })
      .then((resultat) => {
        resultat.data.map((r) => {
          return (r.image =
            "data:image/jpeg;base64," +
            new Buffer(r.image.data.data).toString("base64"));
        });
        setData(resultat.data);
      });
  };

  useEffect(() => {
    setCategories([{ title: "Tout" }]);
    setMarques([{ title: "Tout" }]);
    axios
      .post("http://localhost:4000/categories/recherche", {})
      .then((resultat) => {
        resultat.data.forEach((d) =>
          setCategories((categorie) => [...categorie, { title: d.nom }])
        );
      });
    axios
      .post("http://localhost:4000/marques/recherche", {})
      .then((resultat) => {
        resultat.data.forEach((d) =>
          setMarques((marque) => [...marque, { title: d.nom }])
        );
      });
    handleData("Tout");
  }, []);

  return (
    <Grid container direction="row">
      <Grid item className={classes.formControl}>
        <FormControl>
          <div className={classes.padding}>
            <FormLabel>Prix</FormLabel>
            <RadioGroup
              defaultValue={"Tout"}
              onChange={(e) => {
                handleData(e.target.value);
              }}
            >
              <FormControlLabel value="Tout" control={<Radio />} label="Tout" />
              <FormControlLabel
                value="de 0 à 25"
                control={<Radio />}
                label="de 0 à 25"
              />
              <FormControlLabel
                value="de 25 à 50"
                control={<Radio />}
                label="de 25 à 50"
              />
              <FormControlLabel
                value="de 50 à 100"
                control={<Radio />}
                label="de 50 à 100"
              />
              <FormControlLabel
                value="plus que 100"
                control={<Radio />}
                label="plus que 100"
              />
            </RadioGroup>
          </div>

          <div className={classes.padding}>
            <FormLabel>Catégorie</FormLabel>
            <Autocomplete
              defaultValue={{ title: "Tout" }}
              id="categorie"
              onInputChange={(event, value) => setCategorie(value)}
              options={categories}
              getOptionLabel={(option) => option.title}
              renderInput={(params) => (
                <TextField {...params} margin="normal" variant="outlined" />
              )}
            />
          </div>

          <div className={classes.padding}>
            <FormLabel>Marque</FormLabel>
            <Autocomplete
              id="categorie"
              onInputChange={(event, value) => {
                setMarque(value);
                console.log(event.target.value);
              }}
              defaultValue={{ title: "Tout" }}
              options={marques}
              getOptionLabel={(option) => option.title}
              renderInput={(params) => (
                <TextField {...params} margin="normal" variant="outlined" />
              )}
            />
          </div>
        </FormControl>
      </Grid>
      <Grid item className={classes.formControl} xs={true}>
        <GridList cols={false}>
          {data.map((d) => (
            <Paper className={classes.Box}>
              <img alt={d.nom} src={d.image} className={classes.image}></img>
              <Typography className={classes.Text} noWrap="true">
                <Link href={"/Article/" + d._id}> {d.nom} </Link>
              </Typography>
              <Typography
                color="secondary"
                variant="h6"
                className={classes.Text}
                noWrap="true"
              >
                {d.prix} DT
              </Typography>
            </Paper>
          ))}
        </GridList>
      </Grid>
    </Grid>
  );
}
