import React, { useEffect, useState } from "react";
import { Container, Button } from "@material-ui/core";
import axios from "axios";

export default function Panier() {
  const [items, setItems] = useState([]);

  const getPanier = async () => {
    return await axios.post(
      "http://localhost:4000/clients/getpanier",
      {},
      {
        headers: {
          Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
        },
      }
    );
  };

  const getData = async (id) => {
    return await axios.get("http://localhost:4000/articles/recherche/" + id);
  };

  const setData = async (resultat, d) => {
    await setItems((items) => [
      ...items,
      {
        nom: resultat.data.nom,
        quantite: d.quantite,
        prix: resultat.data.prix,
      },
    ]);
  };

  useEffect(() => {
    getPanier().then((resultat) =>
      resultat.data.forEach((d) =>
        getData(d._id).then((resultat) => setData(resultat, d))
      )
    );
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!localStorage.getItem("jwt-cookie")) {
      window.location = "/Connexion";
    }
  };

  return (
    <Container component="main">
      <form onSubmit={handleSubmit} noValidate>
        <Button variant="contained" color="primary" type="submit">
          Commander
        </Button>
      </form>
    </Container>
  );
}
