import React, { useState } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import axios from "axios";

const useStyles = makeStyles(theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1)
  },
  submit: {
    margin: theme.spacing(3, 0, 2)
  }
}));

export default function Connexion() {
  const [email, setEmail] = useState("");
  const [passe, setPasse] = useState("");

  const handleSubmit = event => {
    event.preventDefault();
    axios
      .post("http://localhost:4000/clients/authentification", {
        email: email,
        passe: passe
      })
      .then(resultat => {
        if (!(resultat.data === "Email ou Mot de passe non valide")) {
          localStorage.setItem("jwt-cookie", resultat.data);
        }
      })
      .then(() => {
        if (localStorage.getItem("jwt-cookie")) window.location = "/";
      });
  };

  const classes = useStyles();

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Connexion
        </Typography>
        <form onSubmit={handleSubmit} className={classes.form} noValidate>
          <TextField
            value={email}
            onChange={event => setEmail(event.target.value)}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email"
            name="email"
            autoComplete="email"
            autoFocus
          />
          <TextField
            value={passe}
            onChange={event => setPasse(event.target.value)}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="passe"
            label="Mot de passe"
            type="password"
            id="passe"
            autoComplete="passe"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Connexion
          </Button>
        </form>
      </div>
    </Container>
  );
}
