import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Typography,
  Grid,
  Button,
  makeStyles,
  TextField
} from "@material-ui/core";
import Autocomplete from "@material-ui/lab/Autocomplete";

const useStyles = makeStyles(theme => ({
  spacing: {
    margin: theme.spacing(1)
  }
}));

export default function Article() {
  const classes = useStyles();
  const [data, setData] = useState("");
  const [quantite, setQuantite] = useState({});
  useEffect(() => {
    axios
      .get(
        "http://localhost:4000/articles/recherche/" +
          window.location.pathname.substring(9, window.location.pathname.length)
      )
      .then(resultat => {
        setData(resultat.data);
        setQuantite(1 - data.quantite);
      });
  }, []);
  return (
    <Grid container xs={3} direction="column" className={classes.spacing}>
      <Typography variant="h5" className={classes.spacing}>
        {data.nom}
      </Typography>
      <Typography variant="h4" color="secondary" className={classes.spacing}>
        {data.prix} DT
      </Typography>
      <Autocomplete
        id="combo-box-demo"
        options={quantite}
        getOptionLabel={option => option.title}
        renderInput={params => (
          <TextField {...params} label="Combo box" variant="outlined" />
        )}
      />
      <Button variant="contained" color="primary" className={classes.spacing}>
        Ajouter au panier
      </Button>
    </Grid>
  );
}
