import React from "react";
import { Route, Redirect } from "react-router-dom";
import axios from "axios";

export default function ModerateurRoute({ component: Component, ...rest }) {
  const direction = () => {
    const r = false;
    if (localStorage.getItem("jwt-cookie"))
      axios
        .post(
          "http://localhost:4000/clients/estmoderateur",
          {},
          {
            headers: {
              Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
            },
          }
        )
        .then((resultat) => (r = resultat.data));
    return r;
  };
  return (
    <Route
      {...rest}
      render={(props) => {
        if (direction()) return <Component {...props} />;
        else
          return (
            <Redirect to={{ pathname: "/", state: { from: props.location } }} />
          );
      }}
    />
  );
}
