import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  table: {
    minWidth: 650,
  },
  formcontrol: {
    margin: theme.spacing(1),
  },
}));

export default function Commandes() {
  const classes = useStyles();
  const [etat, setEtat] = useState("Attente");
  const [data, setData] = useState([]);
  useEffect(() => {
    setData([]);
    axios
      .post(
        "http://localhost:4000/commandes/recherche",
        { etat },
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
          },
        }
      )
      .then((resultat) => {
        resultat.data.forEach((r) => {
          setData((d) => [
            ...d,
            {
              adresse: r.adresse,
              client: r.client,
              liste: r.liste,
              prix: r.prix,
            },
          ]);
        });
      });
  }, [etat]);
  return (
    <div>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Client</TableCell>
              <TableCell>Liste des Articles</TableCell>
              <TableCell>PrixTotal</TableCell>
              <TableCell>Adresse</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((d) => (
              <TableRow>
                <TableCell>
                  <div>Nom: {d.client.nom}</div>
                  <div>Prénom: {d.client.prenom}</div>
                  <div>E-mail: {d.client.email}</div>
                  <div>Téléphone: {d.client.telephone}</div>
                </TableCell>
                <TableCell>
                  {d.liste.map((l) => (
                    <div>
                      {l.nom} : {l.prix} DT x{l.quantite}
                    </div>
                  ))}
                </TableCell>
                <TableCell>{d.prix} DT</TableCell>
                <TableCell>{d.adresse}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
