import React from "react";
import {
  makeStyles,
  MuiThemeProvider,
  createMuiTheme
} from "@material-ui/core/styles";
import {
  AppBar,
  Toolbar,
  Button,
  IconButton,
  Typography
} from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import { indigo } from "@material-ui/core/colors";
const theme = createMuiTheme({
  palette: {
    primary: {
      main: indigo[700]
    }
  }
});

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  menuButton: {
    marginRight: theme.spacing(2)
  },
  title: {
    flexGrow: 1
  }
}));

export default function NavBar(props) {
  const classes = useStyles();
  const Auth = localStorage.getItem("jwt-cookie");
  const clientLinks = (
    <div>
      <Button
        onClick={async () => {
          await localStorage.removeItem("jwt-cookie");
          window.location = "/Connexion";
        }}
        color="inherit"
      >
        DÃ©connexion
      </Button>
    </div>
  );
  const inscriptionLink = (
    <Button onClick={() => (window.location = "/Inscription")} color="inherit">
      Inscription
    </Button>
  );

  const connexionLink = (
    <Button onClick={() => (window.location = "/Connexion")} color="inherit">
      Connexion
    </Button>
  );
  const guestLinks = (
    <div>
      {window.location === "/Inscription" ? connexionLink : inscriptionLink}
    </div>
  );

  return (
    <MuiThemeProvider theme={theme}>
      <div className={classes.root}>
        <AppBar position="static" color="primary">
          <Toolbar>
            <IconButton
              edge="start"
              className={classes.menuButton}
              color="inherit"
              aria-label="menu"
            >
              <MenuIcon />
            </IconButton>
            <Typography
              onClick={() => {
                window.location = "/";
              }}
              className={classes.title}
              color="inherit"
            >
              Logo
            </Typography>
            {!Auth ? guestLinks : clientLinks}
          </Toolbar>
        </AppBar>
      </div>
    </MuiThemeProvider>
  );
}
