import React from "react";
import {
  makeStyles,
  MuiThemeProvider,
  createMuiTheme,
  ClickAwayListener,
  Grow,
  Paper,
  Popper,
  MenuItem,
  MenuList
} from "@material-ui/core/styles";
import { AppBar, Toolbar, Button, Typography } from "@material-ui/core";
import {
  HomeRounded,
  ShoppingCartRounded,
  AccountCircleRounded
} from "@material-ui/icons";
import { indigo } from "@material-ui/core/colors";
const theme = createMuiTheme({
  palette: {
    primary: {
      main: indigo[500]
    }
  }
});

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  menuButton: {
    marginRight: theme.spacing(2)
  },
  title: {
    flexGrow: 1
  },
  paper: {
    marginRight: theme.spacing(2)
  }
}));

export default function NavBar(props) {
  const classes = useStyles();
  const Auth = localStorage.getItem("jwt-cookie");
  const clientLinks = (
    <div>
      <Button
        onClick={async () => {
          await localStorage.removeItem("jwt-cookie");
          window.location = "/Historique";
        }}
        color="inherit"
      >
        Historique
      </Button>
      <Button
        onClick={async () => {
          await localStorage.removeItem("jwt-cookie");
          window.location = "/Compte";
        }}
        color="inherit"
      >
        Compte
      </Button>
      <Button
        onClick={async () => {
          await localStorage.removeItem("jwt-cookie");
          window.location = "/Connexion";
        }}
        color="inherit"
      >
        DÃ©connexion
      </Button>
    </div>
  );
  const visiteurLinks = (
    <div>
      <Button
        onClick={() => (window.location = "/Inscription")}
        color="inherit"
      >
        Inscription
      </Button>
      <Button onClick={() => (window.location = "/Connexion")} color="inherit">
        Connexion
      </Button>
    </div>
  );

  return (
    <MuiThemeProvider theme={theme}>
      <div className={classes.root}>
        <AppBar position="static" color="primary">
          <Toolbar>
            <Typography className={classes.title} color="inherit">
              Logo
            </Typography>
            <Button
              onClick={() => {
                window.location = "/";
              }}
              color="inherit"
            >
              <HomeRounded />
            </Button>
            <Button color="inherit">
              <AccountCircleRounded />
            </Button>
            <Button
              onClick={() => {
                window.location = "/Panier";
              }}
              color="inherit"
            >
              <ShoppingCartRounded />
            </Button>
          </Toolbar>
        </AppBar>
      </div>
    </MuiThemeProvider>
  );
}
