import React, { useState } from "react";
import { Route, Redirect } from "react-router-dom";
import axios from "axios";

export default function ModerateurRoute({ component: Component, ...rest }) {
  const [direction, setDirection] = useState(null);
  return (
    <Route
      {...rest}
      render={(props) => {
        setDirection(<Component {...props} />);
        if (localStorage.getItem("jwt-cookie")) {
          axios
            .post(
              "http://localhost:4000/clients/estmoderateur",
              {},
              {
                headers: {
                  Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
                },
              }
            )
            .then((resultat) => {
              if (resultat.data)
                setDirection(
                  <Redirect
                    to={{ pathname: "/", state: { from: props.location } }}
                  />
                );
            });
        }
        return direction;
      }}
    />
  );
}
