import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import {
  Typography,
  Grid,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormLabel,
  FormControl,
  GridList,
  Paper,
  Link,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(3, 3, 3, 1),
  },
  Box: {
    margin: theme.spacing(1),
    width: 200,
  },
  Text: {
    margin: theme.spacing(0, 1),
    textAlign: "center",
  },
  image: {
    maxWidth: 195,
    height: 125,
    display: "block",
    margin: "auto",
  },
}));

export default function Acceuil() {
  const classes = useStyles();
  const [data, setData] = useState([]);
  const [prix, setPrix] = useState("");

  useEffect(() => {
    axios
      .post("http://localhost:4000/articles/recherche", {
        prix: prix,
      })
      .then((resultat) => {
        resultat.data.map((r) => {
          return (r.image =
            "data:image/jpeg;base64," +
            new Buffer(r.image.data.data).toString("base64"));
        });
        setData(resultat.data);
      });
  }, [prix]);

  return (
    <Grid container direction="row">
      <Grid item driection="column" className={classes.formControl}>
        <FormControl>
          <FormLabel>Prix</FormLabel>
          <RadioGroup
            defaultValue={"Tout"}
            onChange={(e) => {
              setPrix(e.target.value);
            }}
          >
            <FormControlLabel value="Tout" control={<Radio />} label="Tout" />
            <FormControlLabel
              value="de 0 à 25"
              control={<Radio />}
              label="de 0 à 25"
            />
            <FormControlLabel
              value="de 25 à 50"
              control={<Radio />}
              label="de 25 à 50"
            />
            <FormControlLabel
              value="de 50 à 100"
              control={<Radio />}
              label="de 50 à 100"
            />
            <FormControlLabel
              value="plus que 100"
              control={<Radio />}
              label="plus que 100"
            />
          </RadioGroup>
          <br/>
          <FormLabel>Catégorie</FormLabel>
          <Autocomplete
            id="marque"
            onInputChange={(event, value) => setMarque(value)}
            defaultValue={{ title: marque }}
            options={marques}
            getOptionLabel={(option) => option.title}
            renderInput={(params) => (
              <TextField
                {...params}
                label="Marque"
                margin="normal"
                variant="outlined"
              />
            )}
          />
          <FormLabel>Marque</FormLabel>
          <Autocomplete
            id="marque"
            onInputChange={(event, value) => setMarque(value)}
            defaultValue={{ title: marque }}
            options={marques}
            getOptionLabel={(option) => option.title}
            renderInput={(params) => (
              <TextField
                {...params}
                label="Marque"
                margin="normal"
                variant="outlined"
              />
            )}
          />
        </FormControl>
      </Grid>
      <Grid item className={classes.formControl} xs={true}>
        <GridList cols={false}>
          {data.map((d) => (
            <Paper className={classes.Box}>
              <img alt={d.nom} src={d.image} className={classes.image}></img>
              <Typography className={classes.Text} noWrap="true">
                <Link href={"/Article/" + d._id}> {d.nom} </Link>
              </Typography>
              <Typography
                color="secondary"
                variant="h6"
                className={classes.Text}
                noWrap="true"
              >
                {d.prix} DT
              </Typography>
            </Paper>
          ))}
        </GridList>
      </Grid>
    </Grid>
  );
}
