import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import { Typography, Grid, GridList, Paper } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(3, 3, 0, 1),
  },
  Box: {
    margin: theme.spacing(1),
    width: 200,
  },
  Text: {
    margin: theme.spacing(0, 1),
    textAlign: "center",
  },
  paper: {
    padding: theme.spacing(2),
    margin: "auto",
    height: 170,
  },
}));

export default function Articles() {
  const classes = useStyles();
  const [data, setData] = useState([]);

  const handleData = () => {
    axios
      .get("http://localhost:4000/articles/recherche", {})
      .then((resultat) => {
        setData(resultat.data);
      });
  };

  useEffect(() => {
    handleData();
  }, []);

  return (
    <Grid item className={classes.formControl} xs={true}>
      <GridList cols={1} spacing={1}>
        {data.map((i) => (
          <Grid item>
            <Paper className={classes.paper}>
              <Typography>{i.nom}</Typography>
              <Typography>{i.prix}DT</Typography>
            </Paper>
          </Grid>
        ))}
      </GridList>
    </Grid>
  );
}
