import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Commandes() {
  const [etat, setEtat] = useState("Attente");
  const [data, setData] = useState([]);
  useEffect(() => {
    setData([]);
    axios
      .post(
        "http://localhost:4000/commandes/recherche",
        { etat },
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
          },
        }
      )
      .then((resultat) => {
        resultat.data.forEach((r) => {
          setData((data) => [
            ...data,
            {
              _id: r._id,
              liste: r.liste,
              client: r.client,
              adresse: r.adresse,
              description: r.description,
            },
          ]);
        });
      });
  }, [etat]);
  return <div>{data.map((d) => d._id)}</div>;
}
