import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import {
  Typography,
  Card,
  CardContent,
  Grid,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormLabel,
  FormControl
} from "@material-ui/core";

const useStyles = makeStyles(theme => ({
  paper: {
    padding: theme.spacing(1),
    minWidth: 200
  },
  formControl: {
    margin: theme.spacing(3)
  }
}));

export default function Acceuil() {
  const classes = useStyles();
  const [data, setData] = useState([]);

  const handleData = value => {
    axios
      .post("http://localhost:4000/articles/recherche", {
        prix: value
      })
      .then(resultat => {
        setData(resultat.data);
      });
  };

  return (
    <Grid container direction="row" className={classes.formControl}>
      <Grid item driection="column">
        <FormControl>
          <FormLabel>Prix</FormLabel>
          <RadioGroup
            defaultValue={"Tout"}
            onChange={e => {
              handleData(e.target.value);
            }}
          >
            <FormControlLabel value="Tout" control={<Radio />} label="Tout" />
            <FormControlLabel
              value="de 0 à 25"
              control={<Radio />}
              label="de 0 à 25"
            />
            <FormControlLabel
              value="de 25 à 50"
              control={<Radio />}
              label="de 25 à 50"
            />
            <FormControlLabel
              value="de 50 à 100"
              control={<Radio />}
              label="de 50 à 100"
            />
            <FormControlLabel
              value="plus que 100"
              control={<Radio />}
              label="plus que 100"
            />
          </RadioGroup>
        </FormControl>
      </Grid>
      <Grid item direction="row">
        {data.map(d => (
          <Grid item xs={2} className={classes.paper}>
            <Card>
              <CardContent>
                <Typography>{d.nom}</Typography>
                <Typography>{d.prix} TND</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Grid>
  );
}
