import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import axios from "axios";

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

export default function Clients() {
  const [rows, setRows] = useState([]);
  useEffect(() => {
    axios
      .post(
        "http://localhost:4000/clients/getclients",
        {},
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
          },
        }
      )
      .then((resultat) => {
        resultat.data.forEach((r) => {
          setRows((rows) => [...rows, { _id: r._id }]);
        });
      });
  }, []);

  const classes = useStyles();

  return (
    // <TableContainer component={Paper}>
    //   <Table className={classes.table} aria-label="simple table">
    //     <TableHead>
    //       <TableRow>
    //         <TableCell>ID</TableCell>
    //         <TableCell align="right">Nom</TableCell>
    //         <TableCell align="right">Prenom</TableCell>
    //         <TableCell align="right">E-mail</TableCell>
    //         <TableCell align="right">Date de création</TableCell>
    //       </TableRow>
    //     </TableHead>
    //     <TableBody>
    //       {rows.map((row) => (
    //         <TableRow>
    //           <TableCell>{row._id}</TableCell>
    //           <TableCell align="right">{row.nom}</TableCell>
    //           <TableCell align="right">{row.prenom}</TableCell>
    //           <TableCell align="right">{row.email}</TableCell>
    //           <TableCell align="right">{row.cree}</TableCell>
    //         </TableRow>

    //       ))}
    //     </TableBody>
    //   </Table>
    // </TableContainer>
    <div>
      {rows.map((row) => (
        <p>{row._id}</p>
      ))}
    </div>
  );
}
