import React from "react";
import {
  makeStyles,
  MuiThemeProvider,
  createMuiTheme
} from "@material-ui/core/styles";
import {
  AppBar,
  Toolbar,
  Button,
  IconButton,
  Typography
} from "@material-ui/core";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import MenuIcon from "@material-ui/icons/Menu";
import { indigo } from "@material-ui/core/colors";
const theme = createMuiTheme({
  palette: {
    primary: {
      main: indigo[500]
    }
  }
});

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  menuButton: {
    marginRight: theme.spacing(2)
  },
  title: {
    flexGrow: 1
  }
}));

export default function NavBar(props) {
  const classes = useStyles();
  const Auth = localStorage.getItem("jwt-cookie");
  const clientLinks = (
    <div>
      <Button
        onClick={async () => {
          await localStorage.removeItem("jwt-cookie");
          window.location = "/Historique";
        }}
        color="inherit"
      >
        Historique
      </Button>
      <Button
        onClick={async () => {
          await localStorage.removeItem("jwt-cookie");
          window.location = "/Compte";
        }}
        color="inherit"
      >
        Compte
      </Button>
      <Button
        onClick={async () => {
          await localStorage.removeItem("jwt-cookie");
          window.location = "/Connexion";
        }}
        color="inherit"
      >
        DÃ©connexion
      </Button>
    </div>
  );
  const visiteurLinks = (
    <div>
      <Button
        onClick={() => (window.location = "/Inscription")}
        color="inherit"
      >
        Inscription
      </Button>
      <Button onClick={() => (window.location = "/Connexion")} color="inherit">
        Connexion
      </Button>
    </div>
  );

  return (
    <MuiThemeProvider theme={theme}>
      <div className={classes.root}>
        <AppBar position="static" color="primary">
          <Toolbar>
            <IconButton
              edge="start"
              className={classes.menuButton}
              color="inherit"
              aria-label="menu"
            >
              <MenuIcon />
            </IconButton>
            <Typography color="inherit">Logo</Typography>
            <div className={classes.title}></div>
            <Button
              onClick={() => {
                window.location = "/";
              }}
              color="inherit"
            >
              Acceuil
            </Button>
            <Button color="inherit">
              <AccountCircleIcon />
            </Button>
            <Button
              onClick={() => {
                window.location = "/Panier";
              }}
              color="inherit"
            >
              Panier
            </Button>
          </Toolbar>
        </AppBar>
      </div>
    </MuiThemeProvider>
  );
}
