import React, { useEffect, useState } from "react";
import axios from "axios";
import { Typography, Grid, Button, makeStyles } from "@material-ui/core";

const useStyles = makeStyles(theme => ({
  grid: {
    margin: theme.spacing(3)
  }
}));

export default function Article() {
  const classes = useStyles();
  const [data, setData] = useState("");
  useEffect(() => {
    axios
      .get(
        "http://localhost:4000/articles/recherche/" +
          window.location.pathname.substring(9, window.location.pathname.length)
      )
      .then(resultat => {
        setData(resultat.data);
      });
  }, []);
  return (
    <Grid container xs={3} direction="column" className={classes.grid}>
      <Typography variant="h5">{data.nom}</Typography>
      <Typography variant="h4" color="secondary">
        {data.prix} DT
      </Typography>
      <Button color="primary">Ajouter au panier</Button>
    </Grid>
  );
}
