import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Commandes() {
  const [etat, setEtat] = useState("Attente");
  const [data, setData] = useState([]);
  useEffect( () => {
    setData([]);
    let liste;
    let prix = 0;
    async function fetchData(){
    const commande = await axios.post(
      "http://localhost:4000/commandes/recherche",
      { etat },
      {
        headers: {
          Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
        },
      }
    )}
    commande.data.forEach(async (c) => {
      liste = [];
      c.liste.forEach(async (l) => {
        const article = await axios.get(
          "http://localhost:4000/articles/recherche/" + l._id
        );
        liste.push({
          nom: article.data.nom,
          prix: article.data.prix,
          quantite: l.quantite,
        });
        prix += article.data.prix * l.quantite;
      });
      const client = await axios.post(
        "http://localhost:4000/clients/recherchesimple",
        c._id,
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
          },
        }
      );
      setData((d) => [
        ...d,
        {
          _id: c._id,
          nom: client.data.nom,
          prenom: client.data.prenom,
          telephone: client.data.telephone,
          email: client.data.email,
          liste: liste,
          prix: prix,
        },
      ]);
    });
  }, [etat]);
  return <div>{JSON.stringify(data)}</div>;
}
