import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import {
  TextField,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@material-ui/core";
import axios from "axios";

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

export default function Clients() {
  const [email ,setEmail] = useState("")
  const [rows, setRows] = useState([]);
  const [open, setOpen] = useState(false);

  const refreshRows = () => {
    axios
      .post(
        "http://localhost:4000/clients/getclients",
        {},
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
          },
        }
      )
      .then((resultat) => {
        resultat.data.forEach((r) => {
          setRows((rows) => [
            ...rows,
            {
              _id: r._id,
              nom: r.nom,
              prenom: r.prenom,
              email: r.email,
              cree: r.cree,
            },
          ]);
        });
      });
  };

  useEffect(() => {
    refreshRows();
  }, []);

  const classes = useStyles();

  return (
    <div>
      <Dialog
        open={open}
        fullWidth
        onClose={() => {
          setOpen(false);
        }}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Modifier</DialogTitle>
        <DialogContent>
          <TextField
            value={nom}
            onChange={event => setNom(event.target.value)}
            variant="outlined"
            margin="dense"
            required
            fullWidth
            id="nom"
            label="Nom"
            name="nom"
            autoComplete="nom"
            autoFocus
          />
        
          <TextField
            value={email}
            onChange={event => setEmail(event.target.value)}
            variant="outlined"
            margin="dense"
            required
            fullWidth
            id="email"
            label="Email"
            name="email"
            autoComplete="email"
            autoFocus
          />
        </DialogContent>
        <DialogActions>
          <Button color="primary">Confirmer</Button>
          <Button
            color="secondary"
            onClick={() => {
              setOpen(false);
            }}
          >
            Annuler
          </Button>
        </DialogActions>
      </Dialog>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Nom</TableCell>
              <TableCell>Prenom</TableCell>
              <TableCell>E-mail</TableCell>
              <TableCell>Date de création</TableCell>
              <TableCell align="center">Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <TableRow>
                <TableCell>{row._id}</TableCell>
                <TableCell>{row.nom}</TableCell>
                <TableCell>{row.prenom}</TableCell>
                <TableCell>{row.email}</TableCell>
                <TableCell>{row.cree}</TableCell>
                <TableCell align="center">
                  <Button
                    color="primary"
                    onClick={() => {
                      setOpen(true);
                    }}
                  >
                    Modifier
                  </Button>
                  <Button
                    color="secondary"
                    onClick={() => {
                      axios
                        .post(
                          "http://localhost:4000/clients/supprimerclient",
                          { _id: row._id },
                          {
                            headers: {
                              Authorization:
                                "bearer " + localStorage.getItem("jwt-cookie"),
                            },
                          }
                        )
                        .then(() => {
                          setRows([]);
                          refreshRows();
                        });
                    }}
                  >
                    Supprimer
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
