import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  Button,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  table: {
    minWidth: 650,
  },
  formcontrol: {
    margin: theme.spacing(1),
  },
  btnSpacing: {
    margin: theme.spacing(1, 2, 2, 1),
  },
}));

export default function Commandes() {
  const classes = useStyles();
  const [etat, setEtat] = useState("Attente");
  const [data, setData] = useState([]);
  useEffect(() => {
    setData([]);
    axios
      .post(
        "http://localhost:4000/commandes/recherche",
        { etat },
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
          },
        }
      )
      .then((resultat) => {
        resultat.data.forEach((r) => {
          setData((d) => [
            ...d,
            {
              _id: r.id,
              adresse: r.adresse,
              client: r.client,
              liste: r.liste,
              prix: r.prix,
            },
          ]);
        });
      });
  }, [etat]);
  return (
    <div>
      <Button
        color="secondary"
        className={classes.btnSpacing}
        onclick={() => {
          setEtat("Attente");
        }}
      >
        en Attente
      </Button>
      <Button
        color="secondary"
        className={classes.btnSpacing}
        onclick={() => {
          setEtat("Confirmé");
        }}
      >
        Confirmés
      </Button>
      <Button
        color="secondary"
        className={classes.btnSpacing}
        onclick={() => {
          setEtat("Annulé");
        }}
      >
        Annulés
      </Button>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell align="left">
                <Typography color="primary">Client</Typography>
              </TableCell>
              <TableCell align="left">
                <Typography color="primary">Liste des Articles</Typography>
              </TableCell>
              <TableCell align="left">
                <Typography color="primary">PrixTotal</Typography>
              </TableCell>
              <TableCell align="left">
                <Typography color="primary">Adresse</Typography>
              </TableCell>
              <TableCell align="center">
                <Typography color="primary">Actions</Typography>
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((d) => (
              <TableRow>
                <TableCell align="left">
                  <div>ID: {d.client._id}</div>
                  <div>Nom: {d.client.nom}</div>
                  <div>Prénom: {d.client.prenom}</div>
                  <div>E-mail: {d.client.email}</div>
                  <div>Téléphone: {d.client.telephone}</div>
                </TableCell>
                <TableCell align="left">
                  {d.liste.map((l) => (
                    <div>
                      {l.nom} x{l.quantite} : {l.prix} DT
                    </div>
                  ))}
                </TableCell>
                <TableCell align="left">{d.prix} DT</TableCell>
                <TableCell align="left">{d.adresse}</TableCell>
                <TableCell align="center">
                  <Button color="primary">Confirmer</Button>
                  <Button color="secondary">Annuler</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
