import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Commandes() {
  const [etat, setEtat] = useState("Attente");
  const [data, setData] = useState([]);
  useEffect(() => {
    setData([]);
    axios
      .post(
        "http://localhost:4000/commandes/recherche",
        { etat },
        {
          headers: {
            Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
          },
        }
      )
      .then((commande) => {
        let liste = [];
        commande.data.forEach((c) => {
          axios
            .post("http://localhost:4000/clients/recherchesimple", c._id, {
              headers: {
                Authorization: "bearer " + localStorage.getItem("jwt-cookie"),
              },
            })
            .then((client) => {
              c.liste
                .forEach((l) =>
                  axios
                    .get("http://localhost:4000/articles/recherche/" + l._id)
                    .then((article) =>
                      liste.push({
                        nom: article.data.nom,
                        prix: article.data.prix,
                        quantite: l.quantite,
                      })
                    )
                )
                .then(() => {
                  setData((d) => [
                    ...d,
                    {
                      _id: c._id,
                      nom: client.data.nom,
                      prenom: client.data.prenom,
                      telephone: client.data.telephone,
                      email: client.data.email,
                      liste: liste,
                    },
                  ]);
                });
            });
        });
      });
  }, [etat]);
  return <div>{JSON.stringify(data)}</div>;
}
