import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import { Typography, Card, CardContent, Grid } from "@material-ui/core";

const useStyles = makeStyles(theme => ({
  paper: {
    padding: theme.spacing(1)
  }
}));

export default function Acceuil() {
  const classes = useStyles();
  const { carte, setCarte } = useState("");

  useEffect(() => {
    axios.post("http://localhost:4000/articles/recherche").then(resultat => {
      // for (let i = 0; i < resultat.data.length; i++) {
      setCarte(
        <Grid item xs={2} className={classes.paper}>
          <Card>
            <CardContent>
              <Typography>{resultat.data[0].nom}</Typography>
              <Typography>{resultat.data[0].quantite}</Typography>
              <Typography>{resultat.data[0].prix}</Typography>
            </CardContent>
          </Card>
        </Grid>
      );
      console.log(carte);
      // }
    });
  });

  return (
    <Grid container direction="row">
      {carte}
    </Grid>
  );
}
